// App-mac.jsx
import React, { useEffect, useState } from 'react';
import QR2 from './qr2';
import MacView from './7mac';

export default function AppMac() {
  const [showResult, setShowResult] = useState(false);

  useEffect(() => {
    const ws = new WebSocket('wss://192.168.10.103:3000');
    ws.onmessage = (event) => {
      try {
        const parsed = JSON.parse(event.data);
        if (parsed.type === 'showResult') {
          setShowResult(true);
        }
        if (parsed.type === 'resetMac') {
          setShowResult(false);
        }
      } catch {}
    };
    return () => ws.close();
  }, []);

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ flex: 1 }}>
        <video src="/movie.mp4" autoPlay loop muted style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
      </div>
      <div style={{ flex: 1 }}>
        {showResult ? <MacView /> : <QR2 />}
      </div>
    </div>
  );
}
