import React, { useEffect, useState } from "react";
import QR2 from "./qr2";
import MacView from "./7mac";

const WS_SERVER_URL = "wss://192.168.10.103:3000";

const AppMac = () => {
  const [showQR2, setShowQR2] = useState(true);

  useEffect(() => {
    const ws = new window.WebSocket(WS_SERVER_URL);
    ws.onmessage = (event) => {
      const msg = JSON.parse(event.data);
      if (msg.type === "show7mac") setShowQR2(false);
      if (msg.type === "showQR2") setShowQR2(true);
    };
    return () => ws.close();
  }, []);

  return (
    <div style={{ display: "flex", width: "100vw", height: "100vh" }}>
      {/* 左：動画 */}
      <div style={{ flex: 1, background: "black" }}>
        <video
          src="/movie.mp4"
          style={{ width: "100%", height: "100%", objectFit: "contain" }}
          autoPlay
          loop
          muted
        />
      </div>
      {/* 右：QR2または7mac */}
      <div style={{ flex: 1, background: "black" }}>
        {showQR2 ? <QR2 /> : <MacView />}
      </div>
    </div>
  );
};

export default AppMac;
